mapper.select:
SELECT ID, PW
FROM TBL_ADMIN
WHERE ID=#{usid} AND PW=#{upw}

mapper.update:
update tbl_admin set id =#{id}, pw =#{pw}
where
admin_no = #{adminNo}

mapper.select:
select * from ( select rownum rnum, admin_no, id, pw, name, regdate from ( select admin_No, id, pw, name, regdate from tbl_admin where admin_no > 0
order by admin_No desc, regdate desc
)) where (rnum >= #{pageStart} AND rnum <= #{pageEnd}) order by admin_no desc, regdate desc
