mapper.update:
MERGE INTO tbl_method_detail USING dual ON (
     step = #{step}
     and method_no = #{methodNo}
)
	WHEN MATCHED THEN 	
		UPDATE SET  
    method = #{method},
    prod_no = #{prodNo}
		
	WHEN NOT MATCHED THEN
		INSERT (detail_no, step, method, method_no, prod_no)
		VALUES(seq_method_detail.nextval, #{step}, #{method}, #{methodNo}, #{prodNo})

mapper.select:
select * from( select rownum rnum, method_no, skin_type, makeup_degree, method_step, regdate from ( select method_no, skin_type, makeup_degree, method_step, regdate from tbl_method where method_no > 0
order by method_no desc, regdate desc
)) where (rnum >= #{pageStart} AND rnum <= #{pageEnd}) order by method_no desc, regdate desc
