mapper.select:
select * from ( select rownum rnum, PROD_NO, NAME, CUS_GENDER, COMP_NAME, PRICE, LINK, IMG, REGDATE, ADMIN_NO, PRODTYPE_NO from ( select PROD_NO, NAME, CUS_GENDER, COMP_NAME, PRICE, LINK, IMG, REGDATE, ADMIN_NO, PRODTYPE_NO from tbl_product where PROD_NO > 0
order by PROD_NO desc, regdate desc
) ) where( rnum >= #{pageStart} AND rnum <= #{pageEnd} ) order by PROD_NO desc, regdate desc
