mapper.select:
		  
		select 
			* 
		from (
		  	select
		  		rownum rnum, PROD_NO, NAME, CUS_GENDER, COMP_NAME, PRICE, LINK, IMG, REGDATE, ADMIN_NO, PRODTYPE_NO
		  	from ( 
		  		
				
			
		
		 	) 
		 where(
		 	rnum >= #{pageStart} AND rnum <= #{pageEnd}
		 	  ) 
		order by
			PROD_NO desc,
			regdate desc
