mapper.select:
select * from( select rownum rnum, prod_no, name, comp_name, img, rating_avg, prodtype_name, category_name from ( select prod.prod_no, prod.name, prod.comp_name, prod.img, rt.rating_avg , pt.name as prodtype_name, ca.category_name from rating_avg_view rt, tbl_product prod, tbl_prod_type pt ,tbl_category ca where prod.prod_no = rt.prod_no and pt.prodtype_no = prod.prodtype_no and ca.category_no = pt.category_no
order by rating_avg desc
)) where (rnum >= #{pageStart} AND rnum <= #{pageEnd}) order by rating_avg desc
